            <div id="messageContainer" class="message"></div>

            <form id="loginForm" onsubmit="handleSubmit(event)">
                <div class="form-group">
                    <label for="email" class="form-label">Email Address</label>
                    <div class="input-wrapper">
                        <input 
                            type="email" 
