    <script>
        // Utility Functions
        function showError(message, elementId = 'messageContainer') {
            const messageEl = document.getElementById(elementId);
            if (messageEl) {
                messageEl.textContent = message;
                messageEl.className = 'message error show';
                setTimeout(() => {
                    messageEl.classList.remove('show');
                }, 5000);
            }
        }

        function showSuccess(message, elementId = 'messageContainer') {
            const messageEl = document.getElementById(elementId);
            if (messageEl) {
                messageEl.textContent = message;
                messageEl.className = 'message success show';
                setTimeout(() => {
                    messageEl.classList.remove('show');
                }, 5000);
            }
        }

        function clearMessages(elementId = 'messageContainer') {
            const messageEl = document.getElementById(elementId);
            if (messageEl) {
                messageEl.classList.remove('show');
                messageEl.textContent = '';
            }
        }

        function setLoading(isLoading, buttonId = 'submitButton') {
            const button = document.getElementById(buttonId);
            if (!button) return;

            if (isLoading) {
                button.disabled = true;
                const buttonText = button.querySelector('.button-text');
                buttonText.innerHTML = '<span class="spinner"></span>Signing in...';
            } else {
                button.disabled = false;
                const buttonText = button.querySelector('.button-text');
                buttonText.textContent = 'Sign In';
            }
        }

        function validateEmail(email) {
            const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            return re.test(email);
        }

        function validatePassword(password) {
            return password.length >= 6;
        }

        // Password Toggle
        function togglePassword() {
            const passwordInput = document.getElementById('password');
            const toggleIcon = document.getElementById('toggleIcon');

            if (passwordInput.type === 'password') {
                passwordInput.type = 'text';
                toggleIcon.textContent = 'üôà';
            } else {
                passwordInput.type = 'password';
                toggleIcon.textContent = 'üëÅÔ∏è';
            }
        }

        // Loading State
        function loading_state(isLoading) {
            setLoading(isLoading);
        }

        // Error Display
        function error_display(message) {
            showError(message);
        }

        // Handle Forgot Password
        function handleForgotPassword(event) {
            event.preventDefault();
            const email = document.getElementById('email').value;

            if (!email) {
                showError('Please enter your email address first');
                document.getElementById('email').focus();
                return;
            }

            if (!validateEmail(email)) {
                showError('Please enter a valid email address');
                return;
            }

            if (confirm('Send password reset instructions to ' + email + '?')) {
                setLoading(true);

                fetch('/api/auth/reset-password', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ email })
                })
                .then(response => response.json())
                .then(data => {
                    setLoading(false);
                    if (data.success || data.message) {
                        showSuccess('Password reset instructions sent to your email');
                    } else {
                        showError(data.error || 'Failed to send reset instructions');
                    }
                })
                .catch(error => {
                    setLoading(false);
                    showError('An error occurred. Please try again.');
                    console.error('Error:', error);
                });
            }
        }

        // Handle Submit - Main Login Function
        async function handleSubmit(event) {
            event.preventDefault();
            clearMessages();

            const email = document.getElementById('email').value.trim();
            const password = document.getElementById('password').value;

            // Validation
            if (!email || !password) {
                showError('Please fill in all fields');
                return;
            }

            if (!validateEmail(email)) {
                showError('Please enter a valid email address');
                document.getElementById('email').classList.add('error');
                return;
            }

            if (!validatePassword(password)) {
                showError('Password must be at least 6 characters long');
                document.getElementById('password').classList.add('error');
                return;
            }

            // Remove error classes
            document.getElementById('email').classList.remove('error');
            document.getElementById('password').classList.remove('error');

            // Set loading state
            loading_state(true);

            try {
                const response = await fetch('/api/subscribe/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ email, password })
                });

                const data = await response.json();

                if (response.ok && data.user) {
                    showSuccess('Login successful! Redirecting...');

                    // Store remember me preference
                    if (document.getElementById('rememberMe').checked) {
                        localStorage.setItem('rememberEmail', email);
                    } else {
                        localStorage.removeItem('rememberEmail');
                    }

                    // Redirect to dashboard after a short delay
                    setTimeout(() => {
                        window.location.href = '/dashboard';
                    }, 1000);
                } else {
                    loading_state(false);
                    error_display(data.error || data.message || 'Invalid email or password');
                }
            } catch (error) {
                loading_state(false);
                error_display('An error occurred. Please try again.');
                console.error('Login error:', error);
            }
        }

        // Initialize page
        document.addEventListener('DOMContentLoaded', function() {
            // Check for saved email
            const savedEmail = localStorage.getItem('rememberEmail');
            if (savedEmail) {
                document.getElementById('email').value = savedEmail;
                document.getElementById('rememberMe').checked = true;
            }

            // Check for URL messages
            const urlParams = new URLSearchParams(window.location.search);
            const message = urlParams.get('message');
            if (message) {
                showSuccess(decodeURIComponent(message));
            }

            // Add input event listeners to remove error class
            document.getElementById('email').addEventListener('input', function() {
                this.classList.remove('error');
            });

            document.getElementById('password').addEventListener('input', function() {
                this.classList.remove('error');
            });

            // Focus email input
            document.getElementById('email').focus();
        });
    </script>
</body>
</html>
